import React, { useState, useEffect } from 'react';
// import Products from './components/Products';
// import Box from './components/Box/Box';
// import Home from './components/Main/NavBar'
// import Product from './components/Product';
import dataFake from './dataFake';
import Routers from './routers'
import apiRequest from './api/productApi';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
// import AddProduct from './components/AddProduct';
function App() {

  const [products, setProducts] = useState(dataFake);
  // const [status, setStatus] = useState(false);
  // const [color, setColor] = useState('green');
  // const onHandleClick = () => {
  //   // setStatus(true);
  //   setColor('red');
  // }
  const onHandleRemove = (id) => {
    const newProducts = products.filter(product => product.id !== id);
    const confirmDelete=window.confirm('Bạn có chắc chắn muốn xóa không');
    if (confirmDelete) setProducts(newProducts)
    setProducts(newProducts);
  }
  const onHandleAdd = (product) => {
    const newProduct = {
      id: products.length + 1,
      ...product
    }
    setProducts([
      ...products,
      newProduct
    ])
  }
   useEffect(() => {
    const getProducts = async () => {
      const response = await apiRequest.getAll();
      setProducts(response.data)
    }
    getProducts();

    const onHandleAdd = async (product) => {
      try {
        const { data } = await apiRequest.create(product);
        setProducts([
          ...products,
          data
        ])
      } catch (error) {
        console.log('failed to request API: ', error)
      }
    }
  })
  return (
    <div className="App">
      <Routers products={products} onRemove={onHandleRemove} onAdd={onHandleAdd}/>

    </div>
  )

}
export default App;
