import React, { useState } from 'react'
import PropTypes from 'prop-types';
import { useParams, useHistory } from 'react-router-dom';
import apiRequest from './../../../../api/productApi';


const EditProduct = ({ products, onUpdate }) => {
    let { id } = useParams();
    let history = useHistory();
    const { register, handleSubmit, watch, errors } = useForm();
    const product = products.find(product => product.id === id);
    const [currentProduct, setCurrentProduct] = useState(product);

    const onHandleSubmit = async() => {
        const { data } = await apiRequest.update(id, product)
        history.push('/admin/products');
    }
    
    return (
        <div>
            <form action="" onSubmit={onHandleSubmit} className="w-50">
                <div className="form-group">
                    <label htmlFor="productName">Tên sản phẩm</label>
                    <input type="text" name="name" value={currentProduct.name} ref={register} className="form-control" />
                </div>
                <div className="form-group">
                    <label htmlFor="productPrice">Giá sản phẩm</label>
                    <input type="text" name="price" value={currentProduct.price} ref={register} className="form-control" />
                </div>
                <div className="form-group">
                    <label htmlFor="productDesc">Mô tả sản phẩm</label>
                    <input type="text" name="desc" value={currentProduct.desc} ref={register}  className="form-control" />
                </div>
                <button className="btn btn-primary">Cập nhật</button>
            </form>
        </div>
    )
}

EditProduct.propTypes = {
    products: PropTypes.array
}

export default EditProduct
