import React, { useState } from 'react'
import PropTypes from 'prop-types';
import { useParams, useHistory } from 'react-router-dom';
import { useForm } from "react-hook-form";
import apiCategory from './../../../../api/categoryApi';

const EditProduct = ({ categories}) => {
    let { id } = useParams();
    let history = useHistory();
    const { register, handleSubmit, errors } = useForm();
    const category = categories.find(category => category.id === id);
    const [currentCate, setCurrentCate] = useState(category);

    const onHandleSubmit = (e) => {
        e.preventDefault();
        onUpdate(currentCate);
        history.push('/admin/categories');
    }
    
    const onHandleChange = e => {
        const { name, value } = e.target;
        setCurrentCate({
            ...currentCate,
            [name]: value
        })
    }
    
    return (
        <div>
            <form action="" onSubmit={handleSubmit(onHandleSubmit)} className="w-50">
                <div className="form-group">
                    <label htmlFor="productName">Tên danh mục</label>
                    <input type="text" name="name" defaultValue={currentCate.name} onChange={onHandleChange} ref={register} className="form-control" />
                </div>
                <div className="form-group">
                    <label htmlFor="productName">Mô tả</label>
                    <input type="text" name="desc" defaultValue={currentCate.desc} onChange={onHandleChange} ref={register} className="form-control" />
                </div>
                <button className="btn btn-primary">Cập nhật</button>
            </form>
        </div>
    )
}

EditProduct.propTypes = {
    products: PropTypes.array
}

export default EditProduct
