import React, {useState} from 'react'
import PropTypes from 'prop-types'
import { useForm } from "react-hook-form";

const Addproduct = ({onAdd}) => {
  const { register, handleSubmit, watch, errors } = useForm();
  // const[valueInput, setValueInput]= useState({});
  // const onHandleChange=(e)=>{
  //   const { name, value } = e.target;
  //   setValueInput({
  //       ...valueInput,
  //         [name]:value
  //     });
  // }
  const onHandleSubmit = (data) => {
    console.log(data);
  } 
  // const onHandleSubmit =(e) =>{
  //     e.preventDefault();
  //     onAdd(valueInput)
  // }
    return (
        
      <div className="container-fluid">
      <h1 className="h3 mb-2 text-gray-800">Add Product</h1>
      <div className="card shadow mb-4">
        <div className="card-body">
        <form onSubmit={handleSubmit(onHandleSubmit)}>
        <div className="form-group">
          <label htmlFor="inputProductName">Tên sản phẩm</label>
          <input type="text" className="form-control" name="name" id="inputProductName" ref={register} />       
        </div>       
        <div className="form-group">
          <label htmlFor="inputProductImage"> Ảnh </label>
          <input type="text" className="form-control" name="image" id="inputProductImage" ref={register({required: true})}/>       
        </div>      
        <div className="form-group">
          <label htmlFor="inputProductPrice">Giá</label>
          <input type="text" className="form-control" name="price" id="inputProductPrice" ref={register({required: true})}/>       
        </div>      

        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
        </div>
      </div>
    </div>
    )
}

Addproduct.propTypes = {

}

export default Addproduct
