                    <label htmlFor="categoryName">Tên danh mục</label>
                    <input type="text" name="name" value={currentCate.name} ref={register} className="form-control" />
                </div>
                <div className="form-group">
                    <label htmlFor="categoryName">Mô tả</label>
                    <input type="text" name="desc" value={currentCate.desc} ref={register} className="form-control" />
                </div>
                <button className="btn btn-primary">Cập nhật</button>
            </form>
        </div>
    )
}

EditCategory.propTypes = {
    products: PropTypes.array
