import React, {useState} from 'react'
import PropTypes from 'prop-types'
import { useForm } from "react-hook-form";
import apiCategory from './../../../../api/categoryApi';
import { Editor } from '@tinymce/tinymce-react';

const AddCategory = ({onAddCate, categories}) => {
    const { register, handleSubmit, errors } = useForm();
    const [desc, setDesc] = useState("");
    const onHandleSubmit = (data) =>{
        const newData = {
            id: Math.random().toString(36).substr(2, 9),
            ...data,
            desc
        }
        console.log(newData)
        onAddCate(newData)
    }
    const handleEditorChange = (content, editor) => {
        setDesc(content);
    }
    return (
        <div className="container-fluid">
      <h1 className="h3 mb-2 text-gray-800">Add Category</h1>
      <div className="card shadow mb-4">
        <div className="card-body">
        <form onSubmit={handleSubmit(onHandleSubmit)}>
        <div className="form-group">
          <label htmlFor="inputProductName">Tên danh mục</label>
          <input type="text" className="form-control" name="name" id="inputProductName" ref={register({required: true, minLength:5})} /> 
          {errors.name && errors.name.type === "required" &&<span>Không để trống</span>}  
          {errors.name && errors.name.type === "minLength" &&<span>Tên không dưới 5 ký tự</span>}        
        </div>    
        <div className="form-group">
          <label htmlFor="inputProductPrice">Mô tả</label>
          <Editor
                        init={{
                            height: 500,
                            images_upload_url: 'postAcceptor.php',
                            plugins: [
                                'advlist autolink lists link image charmap print preview anchor',
                                'searchreplace visualblocks code fullscreen',
                                'insertdatetime media table paste code help wordcount'
                            ],
                            toolbar:
                                'undo redo | formatselect | bold italic backcolor |  image link\
                                alignleft aligncenter alignright alignjustify | \
                                bullist numlist outdent indent | removeformat | help',

                        }}
                        onEditorChange={handleEditorChange}
                    />
        </div>      

        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
        </div>
      </div>
    </div>
    )
}

AddCategory.propTypes = {

}

export default AddCategory
